<div class="main-section"><?php $postID = $post->ID; ?><?php if( $footer ) $postID = 'option'; ?><?php $sections = get_field('section', $postID) ?><?php if( $sections ): ?><?php foreach( $sections as $section ): ?><?php $styles = array(); ?><?php $stylesInner = array(); ?><?php $classes = array('builder-section'); ?><?php $classesInner = array('container-fluid'); ?><?php if( $section['type_de_fond'] == 'Image' && $section['image_de_fond'] ): ?><?php $stylesInner[] = 'background-color: '.$section["couleur_de_fond"] ?><?php $styles[] = 'background-image: url('.$section["image_de_fond"]["url"].')' ?><?php elseif( ($section['type_de_fond'] == 'Image' || $section['type_de_fond'] == 'Couleur') && $section['couleur_de_fond'] ): ?><?php $stylesInner[] = 'background-color: '.$section["couleur_de_fond"] ?><?php elseif( $section['type_de_fond'] == 'Video' && $section['video_de_fond'] ): ?><?php $videoID = youtube_video_id($section['video_de_fond']) ?><?php $classesInner[] = 'container-video' ?><?php endif; ?><?php if( $section['padding'] ) $stylesInner[] = 'padding-top:'.$section["padding"].'px; padding-bottom:'.$section["padding"].'px' ?><?php if( $section['parallax'] ) $classes[] = 'parallax' ?><?php if( $section['type_parallax'] ) $classes[] = 'scroll' ?><div style="<?php echo join($styles,';'); ?>" class="<?php echo join($classes,' '); ?>"><div style="<?php echo join($stylesInner,';'); ?>"><?php if( $section['type_de_fond'] == 'Video' ): ?><div class="video-bg video-muted"><div id="<?php echo $videoID; ?>"></div></div><?php endif; ?><?php if( $section['full_width'] == 'Full' ) $container = 'container-fluid'; ?><?php if( $section['full_width'] == 'CentrÃ©' ) $container = 'container'; ?><div class="<?php echo $container; ?>"><div class="row"><?php $columns = $section['grille']; ?><?php if( $columns ): ?><?php foreach( $columns as $col ): ?><?php $styles = array(); ?><?php $classes = array(); ?><?php $sizes = array($col['xs'], $col['s'], $col['m'], $col['l'], $col['xl']); ?><?php if( $section['goutieres'] == 'Non' ) $sizes[] = 'no-gutters'; ?><div style="<?php echo join($styles,';'); ?>" class="col <?php echo join($sizes,' '); ?>"><?php if( $col['image_de_fond'] ) $styles[] = 'background-image: url('.$col["image_de_fond"]["url"].')' ?><?php if( $col['couleur_de_fond'] ) $styles[] = 'background-color: '.$col["couleur_de_fond"] ?><?php if( $col['couleur_de_fond'] || $col['image_de_fond'] ) $classes[] = 'p-30' ?><div style="<?php echo join($styles,';'); ?>" class="<?php echo join($classes,' '); ?>"><?php foreach( $col['widgets'] as $widget ): ?><?php if( $widget['acf_fc_layout'] ) include('builder/blocs/bloc-'.$widget['acf_fc_layout'].'.php'); ?><?php endforeach; ?></div></div><?php endforeach; ?><?php endif ?></div></div></div></div><?php endforeach; ?><?php endif; ?></div>